import React, { Component } from 'react';
import Chart from 'chart.js/auto';
import ChartDataLabels from 'chartjs-plugin-datalabels';

export default class RevenueChart extends Component 
{
	chartRef = React.createRef();

	componentDidMount() {
		const ctx = this.chartRef.current.getContext("2d");

		new Chart(ctx, {
			type: "bar",
			data: {
				labels: ["2018", "2019", "2020", "2021", "2022",],
				datasets: [{
					data: [4355000,0,4986000,5720000,6044000],
					label: 'Revenue',
					backgroundColor: [
					"#fff",
					"#fff",
					"#fff",
					"#fff",
					"gold"
					],
					borderColor: [
				      'grey',
				      'grey',
				      'grey',
				      'grey',
				      '#000',
				    ],
				    borderWidth: {
				    	left: 5,
				    	top: 1
				    }
				}
				]
			},
			plugins: [ChartDataLabels],
			options: {
				animation: {
					duration: 1500,
					easing: 'easeOutExpo',
					onComplete: function(chart, data)
					{
						chart.data.datasets[0].data[0] = 500;
						//chart.data.datasets = [4355000,4468000,4986000,5720000,6044000];
						//chart.update();
					}
				},
				layout: {
		            padding: 20
		        },
				events: [],
				responsive: true,
    			maintainAspectRatio: false,
			    plugins: {
			        legend: {
			            display: false
			        },
			        datalabels: {
				        color: [
							"grey",
							"grey",
							"grey",
							"grey",
							"#000"
							],
						opacity: [
							0.5,
							0.5,
							0.5,
							0.5,
							1],
				        anchor: 'end',
			            align: 'top',
			            font: {
			                weight: 'normal',
							size: '16'
			            },
			            formatter: function(value, context) 
			            {
		                    return '$' + value.toLocaleString().slice(0, -4)
		                }
				      }
					},
			    scales: {
					x: {
					    grid: {
					      display: false
					    }
					},
					y: {
					      display: false
					}
				},
			}
		});
	}
	render() {
		return (
			<div className='chartRow'>
				<canvas
				id="myChart"
				ref={this.chartRef}
				/>
			</div>
			)
	}
}
