import React, {useState, useEffect} from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { Link, useLocation } from 'react-router-dom';
import './Menu.css';

const setLocalStorage = (path) =>
{
	if (path = '/')
	{
		localStorage.setItem('home', 'active');
		localStorage.setItem('about', '');
	}
	else if (path = '/about')
	{
		localStorage.setItem('home', '');
		localStorage.setItem('about', 'active');
	}
}

export default function Menu() 
{
	const homeClassName='nav-link ' + localStorage.home;
	const letterClassName='nav-link ' + localStorage.about;

	const location = useLocation();

	if (location.pathname == '/')
	{
		localStorage.setItem('home', 'active');
		localStorage.setItem('about', '');
	}
	else if (location.pathname == '/about')
	{
		localStorage.setItem('home', '');
		localStorage.setItem('about', 'active');
	}

	return (

			<Navbar collapseOnSelect expand='lg' bg='dark' variant='custom' fixed='top'>
				<Container fluid>
					<Navbar.Toggle aria-controls="responsive-navbar-nav" />
					<Navbar.Collapse id='responsive-navbar-nav'>
						<Nav className='me-auto'>
						<Link to='/' onClick={()=> setLocalStorage('/')} className={homeClassName}>
							<div className='nav-link-inner'>
			            	 	<span className='chapterNumber'>01</span> Home
					        </div>
					    </Link>
						<Link to='/about' onClick={()=> setLocalStorage('/about')} className={letterClassName}>
							<div className='nav-link-inner'>
			            	 	<span className='chapterNumber'>02</span> Letter to Our Shareholders
					        </div>
					    </Link>
					    <Link to='/financialHighlights' className='nav-link'>
							<div className='nav-link-inner'>
			            	 	<span className='chapterNumber'>03</span> FY22 Financial Highlights
					        </div>
					    </Link>
					    <Link to='/achieving' className='nav-link'>
							<div className='nav-link-inner'>
			            	 	<span className='chapterNumber'>04</span> Achieving Commitments and Delivering Enduring Value
					        </div>
					    </Link>
					    <Link to='/expertiseTechnology' className='nav-link'>
							<div className='nav-link-inner'>
			            	 	<span className='chapterNumber'>05</span> Expertise and Technology for <br/>Mission and Enterprise Customers
					        </div>
					    </Link>
						</Nav>
					</Navbar.Collapse>
				</Container>
			</Navbar>
	);
}
